mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM as rn FROM (SELECT * FROM member WHERE 1=1 AND authority LIKE 'ROLE_MEMBER'
AND (name LIKE '%'|| #{keyword} ||'%' OR memberNo LIKE #{keyword})
ORDER BY memberNo DESC ) A ) WHERE rn BETWEEN #{start} AND #{end}

SELECT * FROM ( SELECT A.*, ROWNUM as rn FROM (SELECT * FROM member WHERE 1=1 AND authority LIKE 'ROLE_DOCTOR'
AND (name LIKE '%'|| #{keyword} ||'%' OR memberNo LIKE #{keyword})
ORDER BY memberNo DESC ) A ) WHERE rn BETWEEN #{start} AND #{end}

mapper.update:
UPDATE member
   SET examination = #{examination}
 WHERE memberNo = #{memberNo}
